$(document).ready(function($){
// hide messages 
$("#error").hide();
$("#show_message").hide();
// on submit...
$('#ajax-form').submit(function(e){
e.preventDefault();
$("#error").hide();
// email required
var email = $("input#email").val();
if(email == ""){
$("#error").fadeIn().text("Correo electrónico requerido.");
$("input#email").focus();
return false;
}
// password required
var password = $("input#password").val();
if(password == ""){
$("#error").fadeIn().text("Se requiere contraseña.");
$("input#password").focus();
return false;
}
// address number required
var address = $("input#address").val();
if(address == ""){
$("#error").fadeIn().text("Dirección requerida.");
$("input#address").focus();
return false;
}
// cell_phone number required
var cell_phone = $("input#cell_phone").val();
if(cell_phone == ""){
$("#error").fadeIn().text("Número celular requerido.");
$("input#cell_phone").focus();
return false;
}
// username number required
var username = $("input#username").val();
if(username == ""){
$("#error").fadeIn().text("Usuario requerido.");
$("input#username").focus();
return false;
}
// ajax
$.ajax({
type:"POST",
url: "ajax/update/profile.php",
data: $(this).serialize(), // get all form field value in serialize form
success: function(){
$("#show_message").fadeIn();
//$("#ajax-form").fadeOut();
}
});
});  
return false;
});